# Create the relational database
# And populate the table that will be referenced
# Wait to populate the exercise table

import sqlite3

conn = sqlite3.connect('exercises-relational-database.db')
c = conn.cursor()

# Exercises TABLE (MAIN) ===== OK
c.execute('''
CREATE TABLE exercises(
exercise_id INTEGER PRIMARY KEY AUTOINCREMENT,
exercise_name TEXT NOT NULL UNIQUE,
utility_id  INTEGER NOT NULL,
mechanic_id INTEGER NOT NULL,
force_id INTEGER NOT NULL,
target_id INTEGER NOT NULL,
synergist_id INTEGER NOT NULL,

FOREIGN KEY (utility_id) REFERENCES utilities (id),
FOREIGN KEY (mechanic_id) REFERENCES mechanics (id),
FOREIGN KEY (force_id) REFERENCES forces (id),
FOREIGN KEY (target_id) REFERENCES targets (id),
FOREIGN KEY (synergist_id) REFERENCES synergists (id)
)''')


# ===========================================================

# Utilities TABLE ===== OK
c.execute('''
CREATE TABLE utilities(
id INTEGER PRIMARY KEY AUTOINCREMENT,
utility TEXT UNIQUE)
          ''')
          
ls1 = [('Basic or Auxiliary',), ('Basic',), ('Auxiliary',), ('Specialized',), ('Power',)]
c.executemany("INSERT INTO utilities (utility) VALUES (?)", ls1)

# c.execute("SELECT * FROM utilities")
# print(c.fetchall())

# ===========================================================

# Mechanics TABLE ===== OK
c.execute('''
CREATE TABLE mechanics(
id INTEGER PRIMARY KEY AUTOINCREMENT,
mechanic TEXT UNIQUE)
''')

ls2 = [('Isolated',), ('Compound',)]
c.executemany("INSERT INTO mechanics (mechanic) VALUES (?)", ls2)

# c.execute("SELECT * FROM mechanics")
# print(c.fetchall())

# ===========================================================

# Forces TABLE ===== OK
c.execute('''
CREATE TABLE forces(
id INTEGER PRIMARY KEY AUTOINCREMENT,
force TEXT UNIQUE)
''')

ls3 = [('Pull',), ('Push & Pull',), ('Push',), ('Dynamic to Static',)]
c.executemany("INSERT INTO forces (force) VALUES (?)", ls3)

# c.execute("SELECT * FROM forces")
